import React from "react";

class Timer extends React.Component {
  constructor() {
    super();
    this.state = {
      minutes1: 0,
      minutes2: 0,
      seconds1: 0,
      seconds2: 0,
    }
  }
  setTime() {
    const { time } = this.props;
    this.setState({
      minutes2: time,
    })
  }
  render() {
    this.setTime();
    const { minutes1, minutes2, seconds1, seconds2 } = this.state;
    return (
      <div>
        <span>{ minutes1 }:{ minutes2 }:{ seconds1 }:{ seconds2 }</span>
      </div>
    );
  }
}

export default Timer;
