import React from 'react';
import './App.css';
import Timer from './Timer';
import InputTimer from './InputTimer';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      start: false,
    }
  }

  toggleButton() {
    this.setState({
      start: !false,
    })
  }

  render(){
    const { start } = this.state;
  return (
    <main>
      <div>
      <button>Começar</button>
      <div>
        { start ? <InputTimer /> : <Timer />}
      </div>
      </div>
    </main>
  );
}
}

export default App;
