import React from "react";

class InputTimer extends React.Component {
  constructor() {
    super();
    this.state = {
      minutes1: 0,
      minutes2: 0,
      seconds1: 0,
      seconds2: 0,
    }
  }

  handleChange({ target }) {
    const name = target.name;
    const value = target.value;
    this.setState({
      [name]: value,
    })
  }
  render() {
    return (
      <label>
          <input name='minutes1' type="number" onChange={ this.handleChange } />
          <input name='minutes2' type="number" onChange={ this.handleChange }/>
          <input name='seconds1' type="number" onChange={ this.handleChange }/>
          <input name='seconds2' type="number" onChange={ this.handleChange }/>
          </label>
    );
  }
}

export default InputTimer;
