import React from "react";

class InputTimer extends React.Component {
  constructor() {
    super();
    this.state = {
      minutes1: 0,
      minutes2: 0,
      seconds1: 0,
      seconds2: 0,
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange({ target }) {
    const name = target.name;
    const value = target.value;
    this.setState({
      [name]: value,
    })
  }
  render() {
    const { minutes1, minutes2, seconds1, seconds2 } = this.state;
    return (
      <label>
          <input value={ minutes1 } name='minutes1' type="number" onChange={ this.handleChange } />
          :
          <input value={ minutes2 } name='minutes2' type="number" onChange={ this.handleChange }/>
          :
          <input value={ seconds1 } name='seconds1' type="number" onChange={ this.handleChange }/>
          :
          <input value={ seconds2 } name='seconds2' type="number" onChange={ this.handleChange }/>
          </label>
    );
  }
}

export default InputTimer;
