import React from 'react';
import './App.css';
import Timer from './Timer';
import BeforeTimer from './BeforeTimer ';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      start: true,
      time: 0,
    }

    this.toggleButton = this.toggleButton.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange({ target }) {
    const name = target.name;
    const value = target.value;
    this.setState({
      [name]: value,    
    });
  }

  toggleButton() {
    this.setState((prevState) => ({
      start: !prevState.start,
    }));
  }

  render(){
    console.log(this)
    const { start, time } = this.state;
  return (
    <main>
      <div>
      <button onClick={ this.toggleButton }>Começar</button>
      <div>
        { start ? (<><BeforeTimer /> <label>Minutos <input onChange={ this.handleChange } name="time" type="number" value={ time } /></label></>) : <Timer time={ time } />}
      </div>
      </div>
    </main>
  )
}
}

export default App;
